#include <windows.h>
#include <stdio.h>
#include <stdlib.h>
#include <time.h>


char buffer[80];
int vspfunc(char *format, ...)
{
   va_list aptr;
   int ret;

   va_start(aptr, format);
   ret = vsprintf(buffer, format, aptr);
   va_end(aptr);

   return(ret);
}

int CALLBACK
WinMain(HINSTANCE Instance,
    HINSTANCE PrevInstance,
    LPSTR     CmdLine,
    int       CmdShow)
{

    srand(time(NULL));
    int score = 0;

    if(IDYES == MessageBox(0,
        "Tu veux un nombre aléatoire ?",
        "Party Junkie V-300",
        MB_YESNO | MB_ICONINFORMATION)){
        vspfunc("Un nombre random entre 0 et 10: %d", rand()%10);
        MessageBox(0,
        buffer,
        "Party Junkie V-300",
        MB_OK | MB_ICONINFORMATION);
    }
    return(0);
}
