    UINT   Message,
    WPARAM WParam,
    LPARAM LParam)
{
switch(Message){
case WM_SIZE:{

}break;
//NOTE(doc): when windows "destroy" our window
case WM_DESTROY:{

}break;
//NOTE(doc): when the user click on the little red "X"
case WM_CLOSE:{

}break;
default:{
DefWindowProc(Window, Message, WParam, LParam);
}break;
}
}
